class Solution:

    @classmethod
    def _is_char_in_string(cls, c: str, string: str) -> int:
        try:
            res = string.index(c)
            return res
        except ValueError:
            return -1

    @classmethod
    def _solution(cls, inp: str, num_of_distinct_chars: int) -> int:
        marker = 0
        index_of_repeated_char = 0
        for index in range(1, len(inp)):
            curr_char = inp[index]
            if index < num_of_distinct_chars:
                if (res := cls._is_char_in_string(curr_char, inp[index_of_repeated_char:index])) != -1:
                    marker = 0
                    index_of_repeated_char = res
                else:
                    marker += 1
            else:
                if (res := cls._is_char_in_string(curr_char, inp[index - 1:index_of_repeated_char:-1])) != -1:
                    index_of_repeated_char = index - (res + 1)
                marker = index - index_of_repeated_char

            if marker == num_of_distinct_chars:
                return index + 1

    def solution1(self, inp: str) -> int:
        return self._solution(inp, 4)

    def solution2(self, inp: str) -> int:
        return self._solution(inp, 14)


